using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.SceneManagement;  public class mainBtn : MonoBehaviour {     //Main에서 버튼 조절할 때 에러나서 새로 만든 스크립트     public static bool GameIsPaused = false;     public static bool isSettings = false;      public static int hintNum = 0;      public GameObject defaultPanelUI;     public GameObject settingCanvasUI;      public void ChangeScene(string sceneName)     {         skipBtn.missionNum = 0;         skipBtn.tutorialNum = 0;         SceneManager.LoadScene(sceneName);     }      public void changeSettings()     {         if (isSettings)         {             playMode();         }         else         {             SoundManager.instance.ButtonClickSound();             settingMode();         }     }      void settingMode()     {         defaultPanelUI.SetActive(false);         settingCanvasUI.SetActive(true);         Time.timeScale = 0f;         isSettings = true;     }      void playMode()     {         defaultPanelUI.SetActive(true);         settingCanvasUI.SetActive(false);         Time.timeScale = 1f;         isSettings = false;     } } 